group.script:
function main() {
    // Base
    if (params.width < 5)
        params.width = 5;

    var center, hole, hole1, hole2, hole3, hole4;

    // parts
    center = cylinder({
        r: params.width / 2,
        h: params.height
    });
    hole = cylinder({
        r: params.width / 12,
        h: params.height
    });

    // transformations
    hole1 = hole.translate([-params.width / 6, params.width / 6, 0]);
    hole2 = hole.translate([params.width / 6, params.width / 6, 0]);
    hole3 = hole.translate([-params.width / 6, -params.width / 6, 0]);
    hole4 = hole.translate([params.width / 6, -params.width / 6, 0]);

    return difference(center, hole1, hole2, hole3, hole4);
}

function main() {
    // Border
    return torus({
        ri: 1,
        ro: params.width / 2,
        fni: 6
    }).translate([0, 0, 0.5]).scale([1, 1, params.height]);
}
