craft.script:
function main() {
	var size = params.size;
    var width = params.width;
    var height = params.height;

    if (width < 5)
        width = 5;

    return union(
        base(),
        border()
    ).scale(size);

    // button components
    function base() {
    	var center, hole, hole1, hole2, hole3, hole4;

        // parts
        center = cylinder({
            r: width / 2,
            h: height
        });
        hole = cylinder({
            r: width / 12,
            h: height
        });

        // transformations
        hole1 = hole.translate([-width / 6, width / 6, 0]);
        hole2 = hole.translate([width / 6, width / 6, 0]);
        hole3 = hole.translate([-width / 6, -width / 6, 0]);
        hole4 = hole.translate([width / 6, -width / 6, 0]);

        return difference(center, hole1, hole2, hole3, hole4);
    }

    function border() {
        return torus({
            ri: 1,
            ro: width / 2,
            fni: 6
        }).translate([0, 0, 0.5]).scale([1, 1, height]);
    }
}
